mapper.select:
SELECT COMMENT_NO, COMMENT_USER_NO, COMMENT_CONTENT, COMMENT_UPDATE_DATE, COMMENT_CREATE_DATE
FROM COMMUNITY_BOARD_COMMENTS
WHERE COMMENT_DELETE_YN = 0
AND COMMUNITY_BOARD_NO = #{communityNo}
ORDER BY COMMENT_CREATE_DATE DESC
LIMIT #{offset}, #{size}

mapper.update:
UPDATE COMMUNITY_BOARD_COMMENTS
SET COMMENT_DELETE_YN = 1
WHERE COMMENT_NO = #{no}
